theme_fields.value:
:root {
  .custom-wizard {
    .wizard-column {
      .wizard-step-contents {
        .wizard-step-form {
          max-height: none;
        }

        .wizard-field {
          margin-bottom: 0.5em;
        }

        .wizard-step-title {
          margin: 0 0 0.5em 0;
        }

        .wizard-step-description {
          margin-bottom: 0.5em;
        }
      }
      .wizard-field {
        .field-description {
          color: black;
        }
      }
    }
  }
}
